import ComponentDrawOption from '../models/ComponentDrawOption';

/**
 * Class that used for parsing.
 * @interface
 */
class DefaultParser {
  /**
   * Default constructor.
   * @param {DefaultData} pluginData - Plugin data storage.
   */
  constructor(pluginData) {
    /**
     * Plugin data storage.
     * @type {DefaultData}
     */
    this.pluginData = pluginData || null;
  }

  /**
   * Convert the content of files into Components.
   * @param {FileInput[]} [inputs=[]] - Data you want to parse.
   */
  parse(inputs = []) { /* eslint no-unused-vars: 0 */ // --> OFF
    this.pluginData.components = [];
    this.pluginData.parseErrors = [];
  }

  /**
   * Set configuration into Components.
   * @param {FileInput} file - Configuration file of components.
   */
  parseConfiguration(file) {
    const config = JSON.parse(file.content);

    if (!config[this.pluginData.name]) {
      return;
    }

    Object.keys(config[this.pluginData.name]).forEach((id) => {
      const component = this.pluginData.getComponentById(id);

      if (!component || !config[this.pluginData.name][id]) {
        return;
      }

      component.drawOption = new ComponentDrawOption(config[this.pluginData.name][id]);
    });
  }

  /**
   * Indicate if this parser can parse this file.
   * @param {FileInformation} fileInformation - File information.
   * @return {Boolean} - Boolean that indicates if this file can be parsed or not.
   */
  isParsable(fileInformation) {
    return fileInformation !== undefined && fileInformation !== null;
  }
}

export default DefaultParser;

/**
 * Class that compile a Component to data.
 * @interface
 */
import FileInput from '../models/FileInput';

class DefaultRender {
  /**
   * Default constructor.
   * @param {DefaultData} pluginData - Plugin data storage.
   */
  constructor(pluginData) {
    /**
     * Plugin data storage.
     * @type {DefaultData}
     */
    this.pluginData = pluginData || null;
  }

  /**
   * Transform all provided components in file inputs.
   * @param {FileInput[]} files - Files managed by the plugin.
   * @return {FileInput[]} - Generated files from components.
   */
  render(files = []) {
    const rendererFiles = files.reduce((acc, file) => {
      acc[file.path] = '';

      return acc;
    }, {});

    this.renderFiles().forEach((file) => {
      rendererFiles[file.path] = file.content;
    });

    return Object.keys(rendererFiles)
      .map((path) => new FileInput({ path, content: rendererFiles[path] }));
  }

  /**
   * Transform all provided components in file inputs.
   * @return {FileInput[]} - Generated files from components.
   */
  renderFiles() {
    return [];
  }

  /**
   * Update configuration file content according to components data.
   * @param {FileInput} file - Configuration file of components.
   */
  renderConfiguration(file) {
    const configuration = {};

    this.pluginData.components
      .filter((component) => component.drawOption)
      .forEach((component) => {
        configuration[component.id] = component.drawOption;
      });

    const config = JSON.parse(file.content) || {};

    config[this.pluginData.name] = configuration;

    file.content = JSON.stringify(config, (key, value) => (value === null ? undefined : value));
  }
}

export default DefaultRender;
